DUMMY_FITS_FILE_PATH = '/data/2019-04-19/CRISP/nb_6173_2019-04-19T17:34:39_scans=0-4_stokes_corrected_export2021-05-28T15:08:12_im.fits'

TEST_FEATURES_VOCABULARY = ["Active Region", "Filament/Prominence", "Quiet Sun", "Polar Region", "Sunspot", "Plage",
                            "Fibril(s)", "Pore", "Jet/Fanjet", "Light Bridge", "Bright Point", "Emerging Flux"]
TEST_EVENTS_VOCABULARY = ["Flare"]


class TestIngestTags(TestCase):
    def setUp(self):
        Instrument.objects.bulk_create([Instrument(name='CHROMIS'), Instrument(name='CRISP')])
        instrument = Instrument.objects.get(name__iexact='CHROMIS')
        path = Path(DUMMY_FITS_FILE_PATH)
        self.data_cube = DataCube.objects.create(oid='2019-04-19T17:34:39_6173_0-4', filename=path.name, path=path, size=10000000, instrument=instrument)

    def test_ingest_tags(self):
        fits_hdu = fits.header.Header.fromstring(TEST_FITS_HEADER)

        ingest_tags(fits_hdu, self.data_cube, TEST_FEATURES_VOCABULARY, TEST_EVENTS_VOCABULARY)

        all_tags = self.data_cube.tags.all()

        self.assertEqual(len(all_tags), 3)
        self.assertTrue(all_tags.filter(name='Sunspot', type=Tag.Type.FEATURE).exists())
        self.assertTrue(all_tags.filter(name='Polar Region', type=Tag.Type.FEATURE).exists())
        self.assertTrue(all_tags.filter(name='Flare', type=Tag.Type.EVENT).exists())
